    static func svgWrapper(_ extraClasses: String, viewBox:String, _ nodes: Node...) -> Node {
        
        .element(named: "svg", nodes: [.attribute(named: "class", value: extraClasses),
                                       .attribute(named: "viewBox", value: viewBox),
                                       .attribute(named: "xmlns", value: "http://www.w3.org/2000/svg"),
                                       .attribute(named: "fill-rule", value: "evenodd"),
